metrics.query:
100 * (
  sum(
    rate(
      label_replace(
        prometheus_remote_write_container_cpu_usage_seconds_total{instance_type="k8s"}[1m],
        "pod", "$1", "container_label_io_kubernetes_pod_name", "(.*)"
      )
    )
  ) by (instance_id, pod)
  /
  (sum(
      prometheus_remote_write_kube_pod_container_resource_limits{instance_type="k8s", resource="cpu", __$labels__}
    ) by (instance_id, pod) * 60)
)
